mapper.update:
update MACHINE
set ID = #{id,jdbcType=DECIMAL},
  MACHINENAME = #{machinename,jdbcType=CHAR},
  COEFFICIENCY = #{coefficiency,jdbcType=CHAR}
where MACHINENO = #{machineno,jdbcType=CHAR}

mapper.select:
select MACHINENO, ID, MACHINENAME, COEFFICIENCY
from MACHINE
where MACHINENO = #{machineno,jdbcType=CHAR}

select m.machineNo as "machineno" ,m.machineName as "machinename" ,f.FStartTime as "fstarttime" ,f.PRecoveryTime as "precoverytime" ,f.FEndTime as "fendtime" ,f.Status as "status" from Machine m inner join FaultyMachine f on m.MachineNo=f.MachineNo
and trim(m.MachineNo) = #{machineno}
and trim(MachineName)= #{machinename}
order by fstarttime desc
