mapper.delete:
delete from PROCESS
where MPSNO = #{mpsno,jdbcType=CHAR}
  and PROCESSNO = #{processno,jdbcType=CHAR}

mapper.insert:
insert into PROCESS (MPSNO, PROCESSNO, SEQUENCENO, 
  MACHINENO, SCHEDULEDSTARTTIME, SCHEDULEDENDTIME, 
  ACTUALSTARTTIME, ACTUALENDTIME, STATUS
  )
values (#{mpsno,jdbcType=CHAR}, #{processno,jdbcType=CHAR}, #{sequenceno,jdbcType=CHAR}, 
  #{machineno,jdbcType=CHAR}, #{scheduledstarttime,jdbcType=CHAR}, #{scheduledendtime,jdbcType=CHAR}, 
  #{actualstarttime,jdbcType=CHAR}, #{actualendtime,jdbcType=CHAR}, #{status,jdbcType=CHAR}
  )

mapper.update:
update PROCESS
set SEQUENCENO = #{sequenceno,jdbcType=CHAR},
  MACHINENO = #{machineno,jdbcType=CHAR},
  SCHEDULEDSTARTTIME = #{scheduledstarttime,jdbcType=CHAR},
  SCHEDULEDENDTIME = #{scheduledendtime,jdbcType=CHAR},
  ACTUALSTARTTIME = #{actualstarttime,jdbcType=CHAR},
  ACTUALENDTIME = #{actualendtime,jdbcType=CHAR},
  STATUS = #{status,jdbcType=CHAR}
where MPSNO = #{mpsno,jdbcType=CHAR}
  and PROCESSNO = #{processno,jdbcType=CHAR}

mapper.select:
select MPSNO, PROCESSNO, SEQUENCENO, MACHINENO, SCHEDULEDSTARTTIME, SCHEDULEDENDTIME, 
ACTUALSTARTTIME, ACTUALENDTIME, STATUS
from PROCESS
where MPSNO = #{mpsno,jdbcType=CHAR}
  and PROCESSNO = #{processno,jdbcType=CHAR}

select MPSNo as "mpsno",SequenceNo as "sequenceno",Process.ProcessNo as "processno",ProcessName as "processname" from Process,carbonknowledge where 1=1
and rtrim(MPSNO) = #{mpsno}
and Process.ProcessNo=carbonknowledge.ProcessNo and rtrim(type) = (select case when rtrim(type)='碳蒙皮' then 'C0001' else 'A0001' end from mps where rtrim(MPSNO) =rtrim(Process.mpsno) ) order by mpsno,cast(sequenceno as integer)

select GA.mpsno, taskno, processfileno, p.processno, processname, wm_concat(m.machineno) machineno,wm_concat(round(ga.proctime/60,1)) proctime from GA,MPS,Process p,CarbonKnowledge c,Machine m where GA.MachineNo=m.ID and GA.mpsno=MPS.mpsno and GA.mpsno=p.mpsno and p.sequenceno=GA.processno and p.processno=c.processno and c.type=(select case when rtrim(type)='碳蒙皮' then 'C0001' else 'A0001' end from mps where rtrim(MPSNO)=#{mpsno})
and rtrim(ga.MPSNO) = #{mpsno}
group by ga.mpsno,taskno,processfileno, p.processno,processname

select m.mpsno,taskno,processfileno,p.processno,processname, wm_concat(c.machineno) machineno from mps m,CarbonKnowledge ck,CarbonMachineWorker c,Process p where m.mpsno=p.mpsno and p.processno=c.processno and ck.processno=c.processno and c.type=(select case when rtrim(type)='碳蒙皮' then 'C0001' else 'A0001' end from mps where rtrim(MPSNO)=#{mpsno}) and c.type=ck.type
and trim(m.mpsno) = #{mpsno}
group by m.mpsno,taskno,processfileno,p.processno,processname
