mapper.update:
update WISDOM.PALMER
set PI1 = #{pi1,jdbcType=DECIMAL},
  PI2 = #{pi2,jdbcType=DECIMAL},
  PI3 = #{pi3,jdbcType=DECIMAL},
  LAMDA = #{lamda,jdbcType=DECIMAL}
where PARTNO = #{partno,jdbcType=CHAR}

mapper.select:
select PARTNO, PI1, PI2, PI3, LAMDA
from WISDOM.PALMER
where PARTNO = #{partno,jdbcType=CHAR}
