mapper.insert:
insert into WISDOM.PROCTIME1 (PARTNO, MACHINENO, PROCESSTIME
  )
values (#{partno,jdbcType=CHAR}, #{machineno,jdbcType=CHAR}, #{processtime,jdbcType=DECIMAL}
  )

mapper.update:
update WISDOM.PROCTIME1
set MACHINENO = #{machineno,jdbcType=CHAR},
  PROCESSTIME = #{processtime,jdbcType=DECIMAL}
where PARTNO = #{partno,jdbcType=CHAR}

mapper.select:
select PARTNO, MACHINENO, PROCESSTIME
from WISDOM.PROCTIME1
where PARTNO = #{partno,jdbcType=CHAR}
