mapper.delete:
delete from WISDOM.PROCTIME
where PARTNO = #{partno,jdbcType=CHAR}
  and MACHINENO = #{machineno,jdbcType=CHAR}

mapper.insert:
insert into WISDOM.PROCTIME (PARTNO, MACHINENO, PROCESSTIME
  )
values (#{partno,jdbcType=CHAR}, #{machineno,jdbcType=CHAR}, #{processtime,jdbcType=DECIMAL}
  )

mapper.update:
update WISDOM.PROCTIME
set PROCESSTIME = #{processtime,jdbcType=DECIMAL}
where PARTNO = #{partno,jdbcType=CHAR}
  and MACHINENO = #{machineno,jdbcType=CHAR}

mapper.select:
select PARTNO, MACHINENO, PROCESSTIME
from WISDOM.PROCTIME
where PARTNO = #{partno,jdbcType=CHAR}
  and MACHINENO = #{machineno,jdbcType=CHAR}
