mapper.delete:
delete from WISDOM.SENSORDATA
where PARTNO = #{partno,jdbcType=CHAR}
  and PROCESSNO = #{processno,jdbcType=CHAR}

mapper.update:
update WISDOM.SENSORDATA
set STARTTIME = #{starttime,jdbcType=TIMESTAMP},
  ENDTIME = #{endtime,jdbcType=TIMESTAMP},
  INTEGRITY = #{integrity,jdbcType=CHAR}
where PARTNO = #{partno,jdbcType=CHAR}
  and PROCESSNO = #{processno,jdbcType=CHAR}

mapper.select:
select PARTNO, PROCESSNO, STARTTIME, ENDTIME, INTEGRITY
from WISDOM.SENSORDATA
where PARTNO = #{partno,jdbcType=CHAR}
  and PROCESSNO = #{processno,jdbcType=CHAR}
