mapper.update:
update WISDOM.USERACCOUNT
set USERNAME = #{username,jdbcType=CHAR},
  PW = #{pw,jdbcType=CHAR},
  USERTYPE = #{usertype,jdbcType=CHAR}
where USERNO = #{userno,jdbcType=CHAR}

mapper.select:
select USERNO, USERNAME, PW, USERTYPE
from WISDOM.USERACCOUNT
where USERNO = #{userno,jdbcType=CHAR}
