mapper.insert:
insert into WISDOM.WORKER (WORKERNO, WORKERNAME, WCOEFFICIENCY
  )
values (#{workerno,jdbcType=CHAR}, #{workername,jdbcType=CHAR}, #{wcoefficiency,jdbcType=CHAR}
  )

mapper.update:
update WISDOM.WORKER
set WORKERNAME = #{workername,jdbcType=CHAR},
  WCOEFFICIENCY = #{wcoefficiency,jdbcType=CHAR}
where WORKERNO = #{workerno,jdbcType=CHAR}

mapper.select:
select WORKERNO, WORKERNAME, WCOEFFICIENCY
from WISDOM.WORKER
where WORKERNO = #{workerno,jdbcType=CHAR}
