mapper.insert:
insert into WISDOM.感知数据 (任务号, 进入工序时间, 离开工序时间, 齐套性情况
  )
values (#{任务号,jdbcType=CHAR}, #{进入工序时间,jdbcType=CHAR}, #{离开工序时间,jdbcType=CHAR}, #{齐套性情况,jdbcType=CHAR}
  )

mapper.update:
update WISDOM.感知数据
set 进入工序时间 = #{进入工序时间,jdbcType=CHAR},
  离开工序时间 = #{离开工序时间,jdbcType=CHAR},
  齐套性情况 = #{齐套性情况,jdbcType=CHAR}
where 任务号 = #{任务号,jdbcType=CHAR}

mapper.select:
select 任务号, 进入工序时间, 离开工序时间, 齐套性情况
from WISDOM.感知数据
where 任务号 = #{任务号,jdbcType=CHAR}
